export const reducers: ReducerTypes&lt;PageState, any&gt;[] = [
on(initSuccess, (state, action): PageState =&gt; {
return {
...state,
metadata: action.metadata,
}
})
];

@Injectable()
export class InitEffect {
constructor(private listMetadataService: ListMetadataService) {}

initEffect$ = createEffect(() =&gt; this.actions$.pipe(
ofType(init),
switchMap(() =&gt; this.listMetadataService
.get&lt;YourListMetadata&gt;('contextName')
.pipe(map((metadata) =&gt; initSuccess({ metadata }))))
));
}
